package AoC2022;

public class DaySix {

    public static void main(String[] args){
        String input = "";
        outerLoop:
        for (int i = 0; i < input.length(); i++){
            String possibleStart = input.substring(i, i+14);
            for(int j = 0; j < possibleStart.length()-1; j++){
                if (possibleStart.lastIndexOf(""+possibleStart.charAt(j)) != j){
                    continue outerLoop;
                }
            }
            System.out.println(i+possibleStart.length());
            return;
        }
    }
}
